"use strict";$.fn.addSliderSegments=function(e,i){return this.each(function(){if("vertical"==i){var s,t="";for(s=1;s<=e-2;s++)t+='<div class="ui-slider-segment" style="top:'+100/(e-1)*s+'%;"></div>';$(this).prepend(t)}else{var r=100/(e-1)+"%",n='<div class="ui-slider-segment" style="margin-left: '+r+';"></div>';$(this).prepend(n.repeat(e-2))}})};